import React from "react"
import logo from "../images/925808_wifi_icon.png"
import "../CSS/questions.css"
import { Button } from "react-bootstrap"
import "bootstrap/dist/css/bootstrap.css"
import {useState} from "react"

export default function Questions (){
    const max =4
    const min = 0
        const z = Math.floor(Math.random() * (max - min + 1)) + min;

        const [score , setScore] = useState(0)
        const [ currentQuestion, setCurrentQuestion] = useState(0);
        const handleAnwserButtonClick= (isCorrect)=> {
            if (isCorrect === true){
                setScore(score + 1)
            }else{
                alert("This is incorrect ðŸ˜•")
            }
            const nextQuestion = currentQuestion +  Math.floor(Math.random()*4)
            setCurrentQuestion(nextQuestion)
        }
        console.log(z)
        const questions1 = [
            {
                questionsText: "What is your name?",
                anwserOptions:[
                    {anwserText: "Armoni", isCorrect:true},
                    {anwserText: "Ayden", isCorrect:false},
                    {anwserText: "Allen", isCorrect:false},
                    {anwserText: "Cassie", isCorrect:false},
                ],
            },

        {
            questionsText: "What is the Capital of North Carolina?",
                anwserOptions:[
                    {anwserText: "Greensboro", isCorrect:true},
                    {anwserText: "Raliegh", isCorrect:false},
                    {anwserText: "Charlotte", isCorrect:false},
                    {anwserText: "Fort Mill", isCorrect:false},
                ],
            },
            {
                questionsText: "What does CSS stand for?",
                anwserOptions:[
                    {anwserText: "Casscading Style Sheet", isCorrect:true},
                    {anwserText: "Casscasing Sheet Style", isCorrect:false},
                    {anwserText: "Computer Sheet Style", isCorrect:false},
                    {anwserText: "Customizeable Style Sheet", isCorrect:false},
                ],
            },
            {
                questionsText: "Which is the correct syntax for importing React?",
                anwserOptions:[
                    {anwserText: "import \"react\" from React", isCorrect:false},
                    {anwserText: "import react from React", isCorrect:false},
                    {anwserText: "import React from \"react\"", isCorrect:true},
                    {anwserText: "import React from react", isCorrect:false},
                ],
            },
            {
                questionsText: "How do you start a reaact app ?",
                anwserOptions:[
                    {anwserText: "npx start-react-app", isCorrect:false},
                    {anwserText: "npm start-react-app", isCorrect:false},
                    {anwserText: "npx start", isCorrect:false},
                    {anwserText: " npm start", isCorrect:true},
                ],
            },
            {
                questionsText: "Inside which HTML element do we put the JavaScript?",
                anwserOptions:[
                    {anwserText: "<script>", isCorrect:false},
                    {anwserText: "<js>", isCorrect:true},
                    {anwserText: "<body>", isCorrect:false},
                    {anwserText: "<javascript> ", isCorrect:false},
                ],
            },
        ]

return(
    <body>
        <div className = "quiz-cont">
            <div id = "container-quest" className = "hide">
                <div id = "question">{questions1[currentQuestion].questionsText}</div>
                <div id = "anwser-buttons" className= "btn-grid">
                {questions1[currentQuestion].anwserOptions.map((anwserOptions) =>(
                <button  onClick = {()=>handleAnwserButtonClick(anwserOptions.isCorrect)}className= "btn">{anwserOptions.anwserText}</button>
                ))}
                </div>

            <div className = "controls-quest">
                <button id = "sub-btn" className = "submit-btn btn2 hide">Submit</button>
                <button id = "nxt-btn" className = "next-btn btn2 hide" onClick= {handleAnwserButtonClick}>Next</button>
            </div>
            <div>
                <h3>Score</h3>
                <p>{score}</p>
            </div>

        </div> 
    </div> 
        </body>

)

}

























